<?php   class Mysql   {      private $hostName = "localhost:3306";      private $usrName = "root";      private $passWord ="893724023";      private $dbname="test";      private $content;      //private $dbname = "test";      public function __construct()      {         $this->content = mysqli_connect($this->hostName,$this->usrName,$this->passWord,$this->dbname);         if(!$this->content)         {            echo "can't connect".mysqli_connect_error();            die();         }      }      //create database      public function createDatabase($sql)      {         if(!mysqli_query($this->content,$sql))         {            echo "error:".mysqli_error($this->content);         }      }      //create table      public function createTable($sql)      {         if(!mysqli_query($this->content,$sql))         {            echo "error:".mysqli_error($this->content);         }      }      //insert table      public function insertTable($mysql)      {         if(!mysqli_query($this->content,$mysql))         {            echo "error:".mysqli_error($this->content);         }      }      //select      public function selectTable($mysql)      {         if(!mysqli_query($this->content,$mysql))         {            echo "error:".mysqli_error($this->content);         }         else {            return mysqli_query($this->content,$mysql);         }      }      public function DeleteTable($mysql)      {         if(!mysqli_query($this->content,$mysql))         {            echo "error:".mysqli_error($this->content);         }      }      public function UpdateTable($mysql)      {         if(!mysqli_query($this->content,$mysql))         {            echo "error:".mysqli_error($this->content);         }      }   }   $con=new Mysql();   /*    * $sql="create table test1(".        "name VARCHAR(100) NOT NULL,".        "id INT NOT NULL);";   */   //$sql="insert into test1(name,id) values ('abc',123);";   //$con->selectTable($sql);?>